sources.assets/sass/_header.scss:
header{
    width: 100vw;
    height: 10vh;
    @include flexbox( row, space-between, center );
    img{
        width: auto;
        height: inherit;
    }
    font-family: $font-normal;
    background-color: $bg-header;
    color: $color-main;
    overflow: hidden;
}
.buscar{
    font-size: map-get($fs, small);
    width: 50%;
    @include flexbox( row, space-between, center );
    input{
        border: $border-search;
        border-radius: $r-search;
        outline: none;
        padding: 5px 10px 5px 15px;
        width: 100%;
    }
    input:focus{
        border-color: $border-color;
    }
    button {
        background: transparent;
        border: none;
        outline: none;
        img{
            width:25px;
            height: 25px;
        }

    }
}
.mobile{
    button{
        background: transparent;
        border: none;
        outline: none;
    }
    button img{
        width: 25px;
        height: 25px;
    }
    float: left;
    overflow: hidden;
}
.mobile .dropbtn{
    cursor: pointer;
    border: none;
    outline: none;
    margin: 0;
}
.link{
    text-decoration: none;
    float:left;
    color: $color-dropdown; 
    text-align: center;
    padding-bottom: 1em;
}
.link:hover{
    color: $color-linkhover;
}
.sub-link {
    padding: 2em;
}
.sub-link a:hover{
    color: $color-linkhover;
}
.dropbtn:focus{
    background-color: $color-linkhover;
}
ul, li{
    text-decoration: none;
    list-style:none;
}
.dropdown-content{
    font-family: $font-normal;
    font-size: map-get($fs, large);
    background: $bg-dropdown;
    opacity: .9 ;
    color: $color-dropdown;
    display:none;
    position: absolute;
    left: 0;
    top: 0;
    width: 100vw;
    height: 100vh;
    z-index: 1;
    padding-left: .5em;
    padding-top: 1em;
}
.dropdown-content .link{
    float: none;
    color: $color-dropdown;
    font-family: $font-normal;
    font-size: map-get($fs, large);
    text-decoration: none;
    text-align: left;
}
.dropdown-content a:hover {
    color: $color-linkhover;
  }
.show{
    display:block;
}

sources.assets/sass/_mixins.scss:
@mixin flexbox( $flex-direction: row, $justify-content, $align-items ){
    display: flex;
    flex-direction: $flex-direction;
    justify-content: $justify-content;
    align-items: $align-items;
}
@mixin grid($n_rows: 1, $n_columns: 1) {
    display: grid;
    grid-template-rows: repeat($n_rows, auto);
    grid-template-columns: repeat($n_columns, auto);
}

sources.assets/sass/_variables.scss:
//Font-sizes
$fs : (
    x-small: 10px,
    small: 12px,
    normal: 16px,
    large: 28px,
    x-large: 36px
);

//  *   MAIN  *
$bg-main: #ffffff;
$color-main: #527466;
$color-title: #245349;
$color-subtitle:#3f7c6f;
$color-linkhover: #54b67d;
//  *   HEADER  *
//Colors Backgrounds
$bg-header: #b9ded6;
$bg-footer: #b9ded6;
$bg-signup: #c9f3db;
$bg-signin: transparent;

//Colors Fonts
$color-signup: #54b67d;
$color-signin: #2b6b48;

//  *   PROMOCIONES  *
//Colors Backgrounds
$bg-promotions: #ecf5f0;

//Colors Fonts
$color-promotions: #24332c;

//  *   VENDER  *
//Colors Backgrounds
$bg-selling: #ecf5f0;

//Colors Fonts
$color-selling: #146b41;

//  *   FAVORITOS  *
//Colors Backgrounds
$bg-cards: #f3feed;

//Colors Fonts
$color-cards: #262922;

//  *   FOOTER  *
//Colors Backgrounds
$bg-footer: #b9ded6;
$bg-copy: #1c2522;

//Colors Fonts
$color-copy: #ffffff;


//Fonts
$font-title: 'Comfortaa', cursive;
$font-normal: 'Assistant', sans-serif;

// * SEARCH BAR *
$border-search: 1px solid #f2fbf9;
$r-search: 10px;
$border-color: #20c5a1;
$bg-search: url('../img/icons/search.png');

//  *   DROPDOWN    *
$bg-dropdown: lighten(#1c2522, .5);
$color-dropdown: #e2f4f0;

//  *   MARGIN  *
$margin: 5px;

//  * AVATAR *
$border-avatar: 1px solid #f2fbf9;

